package Operators_In_JAVA;

public class Ternary_operator {

	

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a =10; 
		int b=20 ; 
		
		
		int x =a>b ? a :b ; // if condition true it will return first expression value, if condition is false it will return 2nd expression value  
				
		System.out.println(x);
	}

}
